//main file where everything connects together.
const express = require("express");
const bodyParser = require("body-parser");
  
//Connecting route files - think of these as different sections of app
const users = require("./routes/users");
const posts = require("./routes/posts");
const error = require("./utilities/error");

// Create app and set which port to use
const app = express();
const port = 3000;

// Understanding the data coming from requests
// (like when someone fills out a form or sends information)
app.use(bodyParser.urlencoded({ extended: true }));
app.use(bodyParser.json({ extended: true }));

// Keeps track of what's happening in app
// like keeping a log of all request that comes in
app.use((req, res, next) => {
    const time = new Date();
    console.log(
      `-----\n${time.toLocaleTimeString()}: Received a ${req.method} request to ${req.url}.`
    );
    if (Object.keys(req.body).length > 0) {
      console.log("Containing the data:");
      console.log(`${JSON.stringify(req.body)}`);
    }
    next();
  });